mapper.update:

update ft_order
set deleted = #{deleted,jdbcType=BOOLEAN}, `uid` = #{uid,jdbcType=BIGINT}, good_id = #{goodId,jdbcType=BIGINT}, school_id = #{schoolId,jdbcType=BIGINT}, num = #{num,jdbcType=INTEGER}, createTime = #{createTime,jdbcType=TIMESTAMP}, payed = #{payed,jdbcType=BOOLEAN}
where id = #{id}

mapper.select:

where fo.deleted = 0
  and fg.deleted = 0
  and fs.deleted = 0
  and fu.deleted = 0
  and fo.payed is not null

    and (
            fg.title like concat('%', #{order.keyword}, '%')
            or fs.school_name like concat('%', #{order.keyword}, '%')
            or fu.name like concat('%', #{order.keyword}, '%')
            or fu.phone = #{order.keyword}
        )


    and fs.id = #{order.schoolId}


    and fg.id = #{order.goodId}


    and fu.id = #{order.uid}

order by fo.id desc
