mapper.delete:

update ft_order
set deleted = 1
where id = #{id}

mapper.select:

where fo.deleted = 0
  and fg.deleted = 0
  and fu.deleted = 0

    and (
                fg.title like concat('%', #{order.keyword}, '%')
            or fh.name like concat('%', #{order.keyword}, '%')
            or fu.name like concat('%', #{order.keyword}, '%')
            or fu.phone = #{order.keyword}
        )


    and a.id = #{order.addressId}


    and fg.id = #{order.goodId}


    and fu.user_id = #{order.uid}


    and fo.status = #{order.status}

order by fo.id desc
